import { Button, Table, TableBody, TableCell, TableContainer, TableHead, TableRow } from "@mui/material";
import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import SSPaper from "../../../components/common/other/SSPaper";
import { useNavigate } from "react-router-dom";
import { STUDENT_ROUTER } from "../../../shared/constants/router/student-router.constant";
import { useAppDispatch } from "../../../app/hooks";
import { deleteStudentEntitiesAsync, searchStudentEntitiesAsync } from "../redux/student.reducer";
import { isFulfilledAction } from "../../../shared/utils/reducer.utils";
import { toast } from "react-toastify";
import { Formik } from "formik";
import * as Yup from "yup";
import { Grid, IconButton, Modal } from "@mui/material";
import { KEY_STORE } from "../../../shared/constants/auth.constant";
import { APP_ROUTER_CONST } from "../../../shared/constants/router/app-router.constant";
import SSBoxModal from "../../../components/common/other/SSBoxModal";
import SSHeaderPage from "../../../components/common/other/SSHeaderPage";
import CloseIcon from "@mui/icons-material/Close";

function ModalDelete({ open, onClose }: { open: boolean; onClose: any }) {
    const navigate = useNavigate();
    const dispatch = useDispatch();
    const logoutUrlStore = localStorage.getItem(KEY_STORE.LOGOUT_URL);

    const handleAcceptedDelete = async () => {
        // await dispatch(logoutAsync("ok"));
        onClose();
        if (logoutUrlStore) {
            window.location.href = logoutUrlStore;
        } else {
            navigate(APP_ROUTER_CONST.login.path, { replace: true });
        }
    };

    return (
        <Modal open={open} onClose={onClose} aria-labelledby="modal-modal-title" aria-describedby="modal-modal-description">
            <SSBoxModal>
                <SSHeaderPage title="Xác nhận xoá">
                    <IconButton onClick={onClose}>
                        <CloseIcon />
                    </IconButton>
                </SSHeaderPage>
                <div className="mt-3 mb-4">Bạn có chắc chắn muốn xoá sinh viên này?</div>
                <Grid container rowSpacing={2} columnSpacing={{ xs: 2, sm: 2, md: 3 }}>
                    <Grid item sm={6} style={{ textAlign: "right" }}>
                        <Button variant="contained" color="info" type="button" onClick={onClose}>
                            Hủy
                        </Button>
                    </Grid>
                    <Grid item sm={6}>
                        <Button variant="contained" color="secondary" type="button" onClick={handleAcceptedDelete}>
                            Đồng ý
                        </Button>
                    </Grid>
                </Grid>
            </SSBoxModal>
        </Modal>
    );
}

const ResultStudent = () => {
    const dispatch = useAppDispatch();

    const students = useSelector((state: any) => state.student.entities);

    const navigate = useNavigate();

    const handleUpdate = async (row: any) => {
        navigate(STUDENT_ROUTER.childrens.updateUser.buildPath(row.id));
        console.log(row, row.id);
    };

    const initialValues = {
        id: "",
    };

    const validationSchema = Yup.object().shape({});

    // Xử lý sự kiện xoá có xác nhận
    const [anchorEl, setAnchorEl] = useState(null);
    const [openLogoutDialog, setOpenLogoutDialog] = useState(false);
    const open = Boolean(anchorEl);

    const handleClick = (event: any) => {
        setAnchorEl(event.currentTarget);
    };

    const handleCloseDialog = (e: any) => {
        setOpenLogoutDialog(false);
    };

    const handleClose = () => {
        setAnchorEl(null);
    };

    const handleClickItem = (event: any) => {
        setAnchorEl(null);
        setOpenLogoutDialog(true);
    };

    const handleDelete = async (row: any, formik: any) => {
        const id = row.id;
        console.log(id);

        const res = await dispatch(deleteStudentEntitiesAsync(id));

        if (isFulfilledAction(res)) {
            toast.success("Xóa thành công");
            dispatch(
                searchStudentEntitiesAsync({
                    name: "",
                })
            );
        } else toast.error("Xóa không thành công");
    };

    return (
        <div>
            {students == null ? null : (
                <Formik initialValues={initialValues} onSubmit={handleDelete} validationSchema={validationSchema}>
                    {(formik) => (
                        <SSPaper>
                            {students.length == 0 ? null : (
                                <div>
                                    <TableContainer>
                                        <Table sx={{ minWidth: 650 }} aria-label="simple table">
                                            <TableHead>
                                                <TableRow>
                                                    <TableCell sx={{ fontSize: "1rem" }}>STT</TableCell>
                                                    <TableCell sx={{ fontSize: "1rem" }}>Mã sinh viên</TableCell>
                                                    <TableCell sx={{ fontSize: "1rem" }}>Họ và tên</TableCell>
                                                    <TableCell sx={{ fontSize: "1rem" }}>Nơi sinh</TableCell>
                                                    <TableCell sx={{ fontSize: "1rem" }}>Năm sinh</TableCell>
                                                    <TableCell sx={{ fontSize: "1rem" }}>Giới tính</TableCell>
                                                    <TableCell sx={{ fontSize: "1rem" }}>Khoa</TableCell>
                                                    <TableCell sx={{ fontSize: "1rem" }}>Thao tác</TableCell>
                                                </TableRow>
                                            </TableHead>
                                            <TableBody>
                                                {students.map((row: any, index: number) => (
                                                    <TableRow
                                                        key={index}
                                                        sx={{ "&:last-child td, &:last-child th": { border: 0 } }}
                                                    >
                                                        <TableCell>{index + 1}</TableCell>
                                                        <TableCell>{row.id}</TableCell>
                                                        <TableCell>
                                                            {row.hoSv} {row.tenSv}
                                                        </TableCell>
                                                        <TableCell>{row.noiSinh}</TableCell>
                                                        <TableCell>{row.ngaySinh}</TableCell>
                                                        <TableCell>{row.phai == true ? "Nam" : "Nữ"}</TableCell>
                                                        <TableCell>{row.khoa.tenKh}</TableCell>
                                                        <TableCell>
                                                            <Button
                                                                variant="outlined"
                                                                size="small"
                                                                onClick={() => {
                                                                    handleUpdate(row);
                                                                }}
                                                            >
                                                                Cập nhật
                                                            </Button>
                                                            &nbsp;
                                                            <Modal>
                                                            <Button
                                                                variant="outlined"
                                                                size="small"
                                                                onClick={() => {
                                                                    handleDelete(row, formik);
                                                                }}
                                                            >
                                                                Delete
                                                            </Button>
                                                            </Modal>
                                                            
                                                        </TableCell>
                                                    </TableRow>
                                                ))}
                                            </TableBody>
                                        </Table>
                                    </TableContainer>
                                </div>
                            )}
                        </SSPaper>
                    )}
                </Formik>
            )}
        </div>
    );
};

export default ResultStudent;
